SELECT
    CONCAT('user', n, '@example.com'),
    '$2b$12$hash',
    ELT(1 + (n % 10), 'Taro','Hanako','Yuki','Kenji','Sakura','Takeshi','Mika','Hiroshi','Yoko','Kazuki'),
    ELT(1 + (n % 10), 'Tanaka','Suzuki','Takahashi','Watanabe','Ito','Yamamoto','Nakamura','Kobayashi','Kato','Yoshida'),
    CONCAT('090-', LPAD(n * 111, 4, '0'), '-', LPAD(n * 123, 4, '0')),
    CONCAT(100 + (n % 900), '-', LPAD(n * 11, 4, '0')),
    ELT(1 + (n % 10), 'Tokyo','Osaka','Kanagawa','Aichi','Saitama','Chiba','Hyogo','Hokkaido','Fukuoka','Kyoto'),
    ELT(1 + (n % 5), 'Shibuya-ku','Shinjuku-ku','Minato-ku','Chiyoda-ku','Meguro-ku'),
    CONCAT(n, '-', (n % 20) + 1, '-', (n % 10) + 1),
    DATE_SUB('2000-01-01', INTERVAL (n * 73) DAY),
    IF(n % 2 = 0, 'male', 'female'),
    ELT(1 + (n % 4), 'regular','regular','silver','gold'),
    DATE_SUB(NOW(), INTERVAL (n * 7) DAY)
FROM (
    SELECT @row := @row + 1 as n FROM
    (SELECT 0 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t1,
    (SELECT 0 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t2,
    (SELECT @row := 10) t3
) numbers
WHERE n <= 100;

